import { Check, CreditCard, Clock, ChefHat, Package, ShoppingBag } from "lucide-react";
import { cn } from "@/lib/utils";

interface OrderStatusProgressProps {
  status: string;
}

const steps = [
  { key: "pending_payment", label: "Payment", icon: CreditCard },
  { key: "payment_submitted", label: "Submitted", icon: Clock },
  { key: "confirmed", label: "Confirmed", icon: Check },
  { key: "ready", label: "Ready", icon: ChefHat },
  { key: "claimed", label: "Claimed", icon: ShoppingBag },
];

const statusOrder = ["pending_payment", "payment_submitted", "confirmed", "ready", "claimed"];

export function OrderStatusProgress({ status }: OrderStatusProgressProps) {
  const currentIndex = statusOrder.indexOf(status);

  if (status === "cancelled") {
    return (
      <div className="flex items-center justify-center py-4">
        <div className="bg-destructive/10 text-destructive px-4 py-2 rounded-lg font-medium">
          Order Cancelled
        </div>
      </div>
    );
  }

  return (
    <div className="w-full py-4">
      <div className="flex items-center justify-between relative">
        <div className="absolute left-0 right-0 top-1/2 -translate-y-1/2 h-1 bg-muted mx-8" />
        <div 
          className="absolute left-0 top-1/2 -translate-y-1/2 h-1 bg-primary mx-8"
          style={{ width: `calc(${(currentIndex / (steps.length - 1)) * 100}% - 4rem)` }}
        />

        {steps.map((step, index) => {
          const isCompleted = index <= currentIndex;
          const isCurrent = index === currentIndex;
          const Icon = step.icon;

          return (
            <div 
              key={step.key} 
              className="flex flex-col items-center z-10"
              data-testid={`step-${step.key}`}
            >
              <div
                className={cn(
                  "w-10 h-10 rounded-full flex items-center justify-center border-2",
                  isCompleted
                    ? "bg-primary border-primary text-primary-foreground"
                    : "bg-background border-muted text-muted-foreground"
                )}
              >
                {isCompleted && index < currentIndex ? (
                  <Check className="h-5 w-5" />
                ) : (
                  <Icon className="h-5 w-5" />
                )}
              </div>
              <span
                className={cn(
                  "text-xs mt-2 font-medium text-center",
                  isCompleted ? "text-foreground" : "text-muted-foreground"
                )}
              >
                {step.label}
              </span>
            </div>
          );
        })}
      </div>
    </div>
  );
}

client/src/components/StarRating.tsx.content:
import { Star } from "lucide-react";
import { cn } from "@/lib/utils";

interface StarRatingProps {
  rating: number;
  maxRating?: number;
  size?: "sm" | "md" | "lg";
  showValue?: boolean;
  count?: number;
  interactive?: boolean;
  onRatingChange?: (rating: number) => void;
  className?: string;
}

export function StarRating({
  rating,
  maxRating = 5,
  size = "md",
  showValue = false,
  count,
  interactive = false,
  onRatingChange,
  className,
}: StarRatingProps) {
  const sizeClasses = {
    sm: "h-3.5 w-3.5",
    md: "h-5 w-5",
    lg: "h-6 w-6",
  };

  const handleClick = (index: number) => {
    if (interactive && onRatingChange) {
      onRatingChange(index + 1);
    }
  };

  return (
    <div className={cn("flex items-center gap-1", className)}>
      <div className="flex">
        {Array.from({ length: maxRating }).map((_, index) => {
          const filled = index < Math.floor(rating);
          const halfFilled = !filled && index < rating;

          return (
            <button
              key={index}
              type="button"
              disabled={!interactive}
              onClick={() => handleClick(index)}
              className={cn(
                "relative",
                interactive && "cursor-pointer hover-elevate"
              )}
              data-testid={`star-${index + 1}`}
            >
              <Star
                className={cn(
                  sizeClasses[size],
                  filled
                    ? "fill-yellow-400 text-yellow-400"
                    : halfFilled
                    ? "text-yellow-400"
                    : "text-muted-foreground/30"
                )}
              />
              {halfFilled && (
                <Star
                  className={cn(
                    sizeClasses[size],
                    "absolute inset-0 fill-yellow-400 text-yellow-400",
                    "clip-path-[inset(0_50%_0_0)]"
                  )}
                  style={{ clipPath: "inset(0 50% 0 0)" }}
                />
              )}
            </button>
          );
        })}
      </div>
      {showValue && (
        <span className="text-sm font-medium ml-1" data-testid="text-rating-value">
          {rating.toFixed(1)}
        </span>
      )}
      {count !== undefined && (
        <span className="text-sm text-muted-foreground" data-testid="text-rating-count">
          ({count})
        </span>
      )}
    </div>
  );
}

